// Test: ADR-016 this. accessor with all primitive types
// Verifies that this. works correctly with every C-Next primitive type inside scope methods

scope AllTypesTest {
    // Unsigned integers
    u8 valU8 <- 255;
    u16 valU16 <- 65535;
    u32 valU32 <- 4294967295;
    u64 valU64 <- 0;

    // Signed integers
    i8 valI8 <- -128;
    i16 valI16 <- -32768;
    i32 valI32 <- -2147483648;
    i64 valI64 <- 0;

    // Floating point
    f32 valF32 <- 3.14;
    f64 valF64 <- 3.141592653589793;

    // Boolean
    bool valBool <- true;

    // Getters using this. accessor - Unsigned integers
    u8 getU8() {
        return this.valU8;
    }

    u16 getU16() {
        return this.valU16;
    }

    u32 getU32() {
        return this.valU32;
    }

    u64 getU64() {
        return this.valU64;
    }

    // Getters using this. accessor - Signed integers
    i8 getI8() {
        return this.valI8;
    }

    i16 getI16() {
        return this.valI16;
    }

    i32 getI32() {
        return this.valI32;
    }

    i64 getI64() {
        return this.valI64;
    }

    // Getters using this. accessor - Floating point
    f32 getF32() {
        return this.valF32;
    }

    f64 getF64() {
        return this.valF64;
    }

    // Getter using this. accessor - Boolean
    bool getBool() {
        return this.valBool;
    }

    // Setters using this. accessor to test assignment
    void setU8(u8 val) {
        this.valU8 <- val;
    }

    void setBool(bool val) {
        this.valBool <- val;
    }

    void setF64(f64 val) {
        this.valF64 <- val;
    }
}

void main() {
    // Test all getter methods using this. accessor
    AllTypesTest.getU8();
    AllTypesTest.getU16();
    AllTypesTest.getU32();
    AllTypesTest.getU64();
    AllTypesTest.getI8();
    AllTypesTest.getI16();
    AllTypesTest.getI32();
    AllTypesTest.getI64();
    AllTypesTest.getF32();
    AllTypesTest.getF64();
    AllTypesTest.getBool();
}
